import React, { useState } from "react"

function Butty(props) {
  const [count, setCount] = useState(0)
  const [name, setName] = useState("Likes")

  function addLike(e) {
    e.preventDefault()
    setCount(count + 1)
  }

  function addName(a) {
    a.preventDefault()
    setName("Like")
  }

  return (
    <div>
      <button onClick={addLike}>
        {count} {props.name}
      </button>
    </div>
  )
}
Butty.defaultProps = {
  name: "Likes"
}

export default Butty
