import React, { useState } from "react"
import validator from "validator"

export default props => {
  const [name, setName] = useState("")
  const [nameError, setnameError] = useState("")
  const [email, setEmail] = useState("")
  const [emailError, setemailError] = useState("")
  const [username, setUsername] = useState("")
  const [usernameError, setusernameError] = useState("")
  const [password, setPassword] = useState("")
  const [passwordError, setpasswordError] = useState("")
  const [website, setWebsite] = useState("")
  const [websiteError, setwebsiteError] = useState("")

  function formSubmit(e) {
    e.preventDefault()

    let valid = true

    if (name === "") {
      valid = false
    }

    if (!validator.isEmail(email)) {
      valid = false
    }

    if (username === "") {
      valid = false
    }

    if (password === "") {
      valid = false
    }

    if (website === "") {
      valid = false
    }

    if (valid) {
      alert("Valid")
    } else {
      alert("invalid")
    }
  }

  return (
    <div>
      <form onSubmit={formSubmit}>
        <label className={nameError ? "error" : ""} htmlFor="name">
          Name
        </label>
        <input
          type="text"
          id="name"
          placeholder="Smitty Werbenjagermanjensen"
          value={name}
          onChange={e => setName(e.target.value)}
        />
        <br />
        <label className={emailError ? "error" : ""} htmlFor="email">
          email
        </label>
        <input
          type="text"
          id="email"
          placeholder="bob@google.com"
          value={email}
          onChange={e => setEmail(e.target.value)}
        />
        <br />
        <label className={usernameError ? "error" : ""} htmlFor="username">
          username
        </label>
        <input
          type="text"
          id="username"
          placeholder="bob@google.com"
          value={username}
          onChange={e => setUsername(e.target.value)}
        />
        <br />
        <label className={passwordError ? "error" : ""} htmlFor="password">
          password
        </label>
        <input
          type="text"
          id="password"
          placeholder="bob@google.com"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <br />
        <label className={websiteError ? "error" : ""} htmlFor="website">
          website
        </label>
        <input
          type="text"
          id="website"
          placeholder="bob@google.com"
          value={website}
          onChange={e => setWebsite(e.target.value)}
        />
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  )
}
