import React, { useState } from "react"

import validator from "validator"

export default props => {
  const [name, setName] = useState("")
  const [nameError, setnameError] = useState("")
  const [email, setEmail] = useState("")
  const [emailError, setemailError] = useState("")
  const [username, setUsername] = useState("")
  const [usernameError, setusernameError] = useState("")
  const [password, setPassword] = useState("")
  const [passwordError, setpasswordError] = useState("")
  const [website, setWebsite] = useState("")
  const [websiteError, setwebsiteError] = useState("")

  function formSubmit(e) {
    e.preventDefault()

    let valid = true

    if (name === "") {
      valid = false
      setnameError("Field cannot be blank.")
    } else {
      setnameError("")
    }

    if (!validator.isEmail(email)) {
      valid = false
      setemailError("Invalid email.")
    } else {
      setemailError("")
    }

    if (username === "") {
      valid = false
      setusernameError("Field cannot be blank.")
    } else {
      setusernameError("")
    }

    if (password === "") {
      valid = false
      setpasswordError("Field cannot be blank.")
    } else {
      setpasswordError("")
    }

    if (website === "") {
      valid = false
      setwebsiteError("Invalid url.")
    } else {
      setwebsiteError("")
    }

    if (valid) {
      alert("Valid")
    } else {
      alert("Invalid")
    }
  }

  return (
    <div>
      <form onSubmit={formSubmit}>
        <label className={nameError ? "error" : ""} htmlFor="name">
          Name {nameError && nameError}
        </label>
        <input
          className={nameError ? "error" : ""}
          type="text"
          id="name"
          placeholder="Smitty Werbenjagermanjensen"
          value={name}
          onChange={e => setName(e.target.value)}
        />
        <br />
        <label className={emailError ? "error" : ""} htmlFor="email">
          email {emailError && emailError}
        </label>
        <input
          className={emailError ? "error" : ""}
          type="text"
          id="email"
          placeholder="bob@google.com"
          value={email}
          onChange={e => setEmail(e.target.value)}
        />
        <br />
        <label className={usernameError ? "error" : ""} htmlFor="username">
          username {usernameError && usernameError}
        </label>
        <input
          className={usernameError ? "error" : ""}
          type="text"
          id="username"
          placeholder="bob@google.com"
          value={username}
          onChange={e => setUsername(e.target.value)}
        />
        <br />
        <label className={passwordError ? "error" : ""} htmlFor="password">
          password {passwordError && passwordError}
        </label>
        <input
          className={passwordError ? "error" : ""}
          type="text"
          id="password"
          placeholder="bob@google.com"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <br />
        <label className={passwordError ? "error" : ""} htmlFor="password">
          password {passwordError && passwordError}
        </label>
        <input
          className={passwordError ? "error" : ""}
          type="text"
          id="password"
          placeholder="bob@google.com"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <br />
        <label className={websiteError ? "error" : ""} htmlFor="website">
          website {websiteError && websiteError}
        </label>
        <input
          className={websiteError ? "error" : ""}
          type="text"
          id="website"
          placeholder="bob@google.com"
          value={website}
          onChange={e => setWebsite(e.target.value)}
        />
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  )
}
