function addItem(name) {
  return {
    type: ADD_ITEM,
    payload: name
  }
}

// 6
export function useItems() {
  const dispatch = useDispatch()
  const items = useSelector(appState => appState.itemState.items)
  const add = item => dispatch(addItem(item))

  useEffect(() => {
    dispatch(getItems())
  }, [])

  return { items, add }
}
